mapper.select:
select date_format(create_time, '%Y-%m-%d') date, count(*) count
from course
where create_time >= #{beginTime} and create_time <= #{endTime}
group by date;

select DATE_FORMAT(CREATE_TIME,'%Y-%m-%d') date,count(*) count
from course_comment
where create_time >= #{beginTime} and create_time <= #{endTime}

	and course_id in (select id from course where teacher_id = #{teacherId})

group by date;
